export interface Client {
  id: string;
  name: string;
  email: string;
  phone: string;
  company?: string;
  address?: string;
  createdAt: Date;
  avatarUrl?: string;
}

export interface Case {
  id: string;
  clientId: string;
  title: string;
  caseNumber: string;
  status: 'open' | 'in-progress' | 'closed' | 'pending';
  priority: 'low' | 'medium' | 'high' | 'urgent';
  practiceArea: string;
  description: string;
  assignedTo: string[];
  createdAt: Date;
  updatedAt: Date;
  courtDate?: Date;
  estimatedValue?: number;
}

export interface TimeEntry {
  id: string;
  caseId: string;
  userId: string;
  description: string;
  hours: number;
  date: Date;
  billable: boolean;
  hourlyRate?: number;
  status: 'draft' | 'submitted' | 'approved' | 'invoiced';
}

export interface Document {
  id: string;
  caseId: string;
  name: string;
  type: string;
  size: number;
  uploadedBy: string;
  uploadedAt: Date;
  url: string;
  category: 'contract' | 'evidence' | 'correspondence' | 'filing' | 'other';
}

export interface Task {
  id: string;
  caseId: string;
  title: string;
  description: string;
  assignedTo: string;
  status: 'todo' | 'in-progress' | 'completed';
  priority: 'low' | 'medium' | 'high';
  dueDate?: Date;
  createdAt: Date;
}

export interface CourtDate {
  id: string;
  caseId: string;
  title: string;
  date: Date;
  location: string;
  judge?: string;
  notes?: string;
  reminderSent: boolean;
}

export interface TeamMember {
  id: string;
  name: string;
  email: string;
  role: 'attorney' | 'paralegal' | 'admin';
  avatarUrl?: string;
  hourlyRate?: number;
}

export interface Note {
  id: string;
  caseId: string;
  content: string;
  createdBy: string;
  createdAt: Date;
}

import { create } from 'zustand';
import { v4 as uuidv4 } from 'uuid';
import { Client, Case, TimeEntry, Document, Task, CourtDate, TeamMember, Note } from '../types';

interface LegalStore {
  clients: Client[];
  cases: Case[];
  timeEntries: TimeEntry[];
  documents: Document[];
  tasks: Task[];
  courtDates: CourtDate[];
  teamMembers: TeamMember[];
  notes: Note[];

  // Client methods
  addClient: (client: Omit<Client, 'id' | 'createdAt'>) => void;
  updateClient: (id: string, client: Partial<Client>) => void;
  deleteClient: (id: string) => void;
  getClientById: (id: string) => Client | undefined;

  // Case methods
  addCase: (caseData: Omit<Case, 'id' | 'createdAt' | 'updatedAt'>) => void;
  updateCase: (id: string, caseData: Partial<Case>) => void;
  deleteCase: (id: string) => void;
  getCaseById: (id: string) => Case | undefined;
  getCasesByClient: (clientId: string) => Case[];

  // Time entry methods
  addTimeEntry: (entry: Omit<TimeEntry, 'id'>) => void;
  updateTimeEntry: (id: string, entry: Partial<TimeEntry>) => void;
